mapper.select:
SELECT geo_lon AS 'geoCoordinates.longitude', geo_lat AS 'geoCoordinates.latitude', geo_provider AS geoProvider, meta_timestamp_received AS timestampReceived, meta_timestamp_recorded AS timestampRecorded, meta_user_id AS userId, meta_device_id AS devideId
FROM data_airprobe
WHERE meta_timestamp_received BETWEEN #{settings.minimumTimestamp} AND #{settings.maximumTimestamp}
AND geo_lon BETWEEN #{west} AND #{east} AND geo_lat BETWEEN #{south} AND #{north}
ORDER BY meta_timestamp_recorded ASC
